var link=document.querySelector(".write_us_btn"),popup=document.querySelector(".modal_content"),close=document.querySelector(".modal_content_close");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal_content_show")}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal_content_show")}),window.addEventListener("keydown",function(e){27==e.keyCode&&popup.classList.contains("modal_content_show")&&popup.classList.remove("modal_content_show")});